import React, {useEffect, useState} from "react";
import "./Card.css";

const Card = (props) => {
  const { children, name, url_pokemon } = props;
  const [pokemon, setPokemon] = useState(null);



    const handleGetPokemon = async (url) => {
      const response = await fetch(url);
      return  await response.json();
  }

    useEffect(() => {
        setPokemon(handleGetPokemon(url_pokemon))
    }, []);



  return (
    <a href={name}>
      <div className="cardWrapper">
        <p className="cardTitle">{children}</p>
          <img className="cardImg"  src={pokemon?.sprites?.front_default} alt={pokemon?.name}/>
      </div>
    </a>
  );
};

export default Card;
