import './main.css'
import {useEffect, useState,} from "react";
import Card from "../components/Card";


const MainPage = () => {
    const [result, setResult] = useState(null)
    const [showImage, setShowImage] = useState(false)

    const handleGetPokemons = async (url = 'https://pokeapi.co/api/v2/pokemon/' ) => {
        const response = await fetch(url);
        const data = await response.json();
        setResult(data)
    }

    const upDateStatus = () => {
        setShowImage(true)
        console.log(showImage)
    }

    useEffect(() => {
        handleGetPokemons()
    }, []);

    useEffect(() => {
        upDateStatus()
    }, [])



    return(
        <>
            <div className="wrapper">
                {result?.results?.map((item, i) => (
                    // <div key={i}>
                    //     <p>{i + 1} - {item.name}</p>
                    // </div>
                    <Card onClick={upDateStatus} url={item.url} key={i}>
                        {item.name}

                    </Card>
                ))}
            </div>

            <div>
                <button  onClick={() => handleGetPokemons(result.previous)}>Prev</button>
                <button onClick={() => handleGetPokemons(result.next)}>Next</button>
                <button onClick={upDateStatus}>UpDateStatus</button>
            </div>
        </>
    )
}


export default MainPage;
